INSERT INTO recipes (title, description, ingredients, instructions, prep_time, cook_time, servings, dosha_effects, thermal_effect, weight_effect, recommended_doshas, seasonal_suitability, difficulty_level, calories_per_serving, rating, rating_count, created_by) VALUES
('Golden Turmeric Milk', 'Traditional Ayurvedic bedtime drink for better sleep and immunity', '["1 cup warm milk", "1 tsp turmeric powder", "1/4 tsp ginger powder", "pinch of black pepper", "1 tsp ghee", "1 tsp honey"]', '["Heat milk gently", "Add turmeric and ginger", "Stir in ghee and black pepper", "Add honey when warm (not hot)", "Serve immediately"]', 5, 5, 1, '{"vata": "balancing", "pitta": "balancing", "kapha": "slightly increasing"}', 'heating', 'light', 'Vata, Pitta', 'Winter, Monsoon', 'Easy', 180, 4.8, 2547, 'Dr. Priya Sharma'),
('Kitchari Bowl', 'Complete Ayurvedic meal that balances all three doshas', '["1 cup basmati rice", "1/2 cup split mung dal", "1 tsp cumin seeds", "1 tsp coriander seeds", "1 inch ginger", "1 tsp turmeric", "2 tbsp ghee", "salt to taste", "4 cups water"]', '["Wash rice and dal separately", "Heat ghee, add cumin and coriander seeds", "Add ginger and turmeric", "Add rice, dal and water", "Cook for 25-30 minutes until soft", "Season with salt"]', 10, 30, 4, '{"vata": "balancing", "pitta": "balancing", "kapha": "balancing"}', 'neutral', 'medium', 'All Doshas', 'All seasons', 'Easy', 320, 4.7, 1829, 'Chef Ramesh Kumar'),
('Cooling Cucumber Raita', 'Refreshing yogurt-based side dish perfect for Pitta constitution', '["1 large cucumber", "1 cup fresh yogurt", "1 tsp roasted cumin powder", "2 tbsp mint leaves", "1 tsp black salt", "1 tsp regular salt"]', '["Grate cucumber and squeeze out excess water", "Whisk yogurt until smooth", "Mix cucumber with yogurt", "Add cumin powder and salts", "Garnish with mint leaves", "Chill before serving"]', 15, 0, 3, '{"vata": "slightly increasing", "pitta": "balancing", "kapha": "increasing"}', 'cooling', 'light', 'Pitta', 'Summer', 'Easy', 85, 4.5, 1234, 'Chef Meera Nair'),
('Warming Ginger Tea', 'Digestive tea that kindles agni and improves circulation', '["2 inch fresh ginger", "2 cups water", "1 tsp honey", "1/4 tsp cinnamon powder", "2-3 cardamom pods"]', '["Slice ginger thinly", "Boil water with ginger and spices", "Simmer for 10 minutes", "Strain the tea", "Add honey when warm", "Serve hot"]', 5, 15, 2, '{"vata": "balancing", "pitta": "slightly increasing", "kapha": "balancing"}', 'heating', 'light', 'Vata, Kapha', 'Winter, Monsoon', 'Easy', 25, 4.6, 987, 'Dr. Anjali Patel'),
('Spiced Lentil Soup', 'Protein-rich soup with digestive spices for optimal nutrition', '["1 cup red lentils", "1 tsp turmeric", "1 tsp cumin seeds", "1 tsp mustard seeds", "2 dry red chilies", "1 inch ginger", "2 tbsp coconut oil", "curry leaves", "salt", "3 cups water"]', '["Wash and cook lentils with turmeric", "Heat oil, add mustard and cumin seeds", "Add curry leaves, chilies, and ginger", "Pour over cooked lentils", "Season with salt", "Simmer for 10 minutes"]', 10, 25, 4, '{"vata": "balancing", "pitta": "neutral", "kapha": "slightly increasing"}', 'heating', 'medium', 'Vata', 'Winter, Monsoon', 'Medium', 180, 4.4, 756, 'Chef Suresh Menon');
